import React, { useState, useEffect } from 'react'
import { useSelector } from 'react-redux'

const Dashboard = () => {
  const { user } = useSelector(state => state.auth)
  const [products, setProducts] = useState([])
  const [orders, setOrders] = useState([])
  const [showAddProduct, setShowAddProduct] = useState(false)
  const [editingProduct, setEditingProduct] = useState(null)
   const [activeTab, setActiveTab] = useState('products')
  // Données du nouveau produit
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    image: '',
    isAvailable: true
  })

  // Charger les produits du restaurant au démarrage
  useEffect(() => {
    loadRestaurantData()
  }, [])

  const loadRestaurantData = () => {
    // Produits mock pour le restaurant connecté
    const mockProducts = [
  {
    id: 1,
    name: "Pâte et Sauce Gombo",
    description: "Pâte de maïs blanc accompagnée d'une onctueuse sauce gombo fraîche et de poisson frit.",
    price: 1500, // Prix en Francs CFA (XOF)
    category: "pâtes-et-tubercules",
    image: "data:image/jpeg;base64,", // Image de plat africain
    isAvailable: true,
    restaurantId: user?.restaurantId || 'resto-parakou'
  },
  {
    id: 2,
    name: "Atassi",
    description: "Mélange de riz et de haricots cuits ensemble, servi avec une sauce tomate et de la viande de bœuf frite.",
    price: 2000,
    category: "céréales-légumineuses",
    image: "data:image/jpeg;base64,", // Image de riz et haricots
    isAvailable: true,
    restaurantId: user?.restaurantId || 'resto-parakou'
  },
  {
    id: 3,
    name: "Poulet Bicyclette Braisé",
    description: "Demi-poulet braisé à la braise, mariné aux épices locales. Servi avec de l'Aloco (banane plantain frite).",
    price: 3500,
    category: "grillades-fritures",
    image: "data:image/jpeg;base64,", // Image de poulet grillé
    isAvailable: true,
    restaurantId: user?.restaurantId || 'resto-parakou'
  },
  {
    id: 4,
    name: "Jus bissap",
    description: "Boisson rafraîchissante et acidulée à base de fleurs d'hibiscus, sucrée et parfumée à la menthe.",
    price: 500,
    category: "boissons-locales",
    image: "data:image/jpeg;base64,",
    isAvailable: false,
    restaurantId: user?.restaurantId || 'resto-parakou'
  }
]

    const mockOrders = [
      { id: 'ORD-001', customer: 'Jean Dupont', items: 'Pizza Margherita x1', total: 1299, status: 'pending', time: '12:30' },
      { id: 'ORD-002', customer: 'Marie Martin', items: 'Pizza Pepperoni x2', total: 3000, status: 'preparing', time: '12:45' }
    ]

    setProducts(mockProducts)
    setOrders(mockOrders)
  }

  const handleAddProduct = (e) => {
    e.preventDefault()
    
    const product = {
      id: Date.now(), 
      ...newProduct,
      price: parseFloat(newProduct.price),
      restaurantId: user?.restaurantId || 'resto-1'
    }

    setProducts(prev => [...prev, product])
    setNewProduct({
      name: '',
      description: '',
      price: '',
      category: '',
      image: '',
      isAvailable: true
    })
    setShowAddProduct(false)
  }

  // Modifier un produit
  const handleEditProduct = (e) => {
    e.preventDefault()
    
    setProducts(prev => prev.map(product =>
      product.id === editingProduct.id 
        ? { ...editingProduct, price: parseFloat(editingProduct.price) }
        : product
    ))
    setEditingProduct(null)
  }

  const handleDeleteProduct = (productId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce produit ?')) {
      setProducts(prev => prev.filter(product => product.id !== productId))
    }
  }

  const toggleAvailability = (productId) => {
    setProducts(prev => prev.map(product =>
      product.id === productId 
        ? { ...product, isAvailable: !product.isAvailable }
        : product
    ))
  }

  // Mettre à jour le statut d'une commande
  const updateOrderStatus = (orderId, newStatus) => {
    setOrders(prev => prev.map(order => 
      order.id === orderId ? { ...order, status: newStatus } : order
    ))
  }

  const getStatusColor = (status) => {
    switch(status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800'
      case 'preparing': return 'bg-blue-100 text-blue-800'
      case 'ready': return 'bg-green-100 text-green-800'
      case 'delivered': return 'bg-gray-100 text-gray-800'
      default: return 'bg-gray-100 text-gray-800'
    }
  }

const categories = ['pâtes-et-tubercules', 'céréales-légumineuses', 'sauces', 'petits-déjeuners', 'grillades-fritures', 'boissons-locales']
  return (
  <div className="min-h-screen bg-gray-50">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 sm:py-8">
      {/* En-tête */}
      <div className="mb-6 sm:mb-8">
        <h1 className="text-2xl sm:text-3xl font-bold text-gray-900">
          Dashboard {user?.restaurantName || 'Mon Restaurant'}
        </h1>
        <p className="text-sm sm:text-base text-gray-600 mt-2">
          {user?.restaurantAddress || 'Gérez vos produits et commandes'}
        </p>
      </div>

      {/* Statistiques */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-6 mb-6 sm:mb-8">
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-md">
          <h3 className="text-sm sm:text-lg font-semibold text-gray-700">Produits actifs</h3>
          <p className="text-2xl sm:text-3xl font-bold text-orange-600">
            {products.filter(p => p.isAvailable).length}
          </p>
        </div>
        
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-md">
          <h3 className="text-sm sm:text-lg font-semibold text-gray-700">Commandes</h3>
          <p className="text-2xl sm:text-3xl font-bold text-blue-600">{orders.length}</p>
        </div>
        
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-md">
          <h3 className="text-sm sm:text-lg font-semibold text-gray-700">En préparation</h3>
          <p className="text-2xl sm:text-3xl font-bold text-yellow-600">
            {orders.filter(o => o.status === 'preparing').length}
          </p>
        </div>
        
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-md">
          <h3 className="text-sm sm:text-lg font-semibold text-gray-700">Chiffre d'affaires</h3>
          <p className="text-2xl sm:text-3xl font-bold text-green-600">
            {orders.reduce((sum, order) => sum + order.total, 0).toFixed(0)}f
          </p>
        </div>
      </div>

      {/* Navigation mobile (onglets) */}
      <div className="lg:hidden mb-6">
        <div className="flex border-b border-gray-200">
          <button
            onClick={() => setActiveTab('products')}
            className={`flex-1 py-3 px-4 text-center font-medium ${
              activeTab === 'products'
                ? 'border-b-2 border-orange-500 text-orange-600'
                : 'text-gray-500'
            }`}
          >
            Produits
          </button>
          <button
            onClick={() => setActiveTab('orders')}
            className={`flex-1 py-3 px-4 text-center font-medium ${
              activeTab === 'orders'
                ? 'border-b-2 border-orange-500 text-orange-600'
                : 'text-gray-500'
            }`}
          >
            Commandes
          </button>
        </div>
      </div>

      {/* Contenu principal */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 sm:gap-8">
        {/* Section Produits - visible sur desktop ou si onglet actif sur mobile */}
        <div className={`bg-white rounded-lg shadow-md p-4 sm:p-6 ${
          activeTab === 'products' ? 'block' : 'hidden lg:block'
        }`}>
          <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center mb-4 sm:mb-6 gap-3">
            <h2 className="text-lg sm:text-xl font-bold text-gray-900">Mes produits</h2>
            <button
              onClick={() => setShowAddProduct(true)}
              className="bg-orange-500 text-white px-3 sm:px-4 py-2 rounded-md hover:bg-orange-600 flex items-center justify-center text-sm sm:text-base"
            >
              <svg className="w-4 h-4 sm:w-5 sm:h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
              Ajouter
            </button>
          </div>

          {/* Formulaire d'ajout - Mobile friendly */}
          {showAddProduct && (
            <div className="mb-6 p-3 sm:p-4 border-2 border-dashed border-orange-300 rounded-lg bg-orange-50">
              <h3 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4">Nouveau produit</h3>
              <form onSubmit={handleAddProduct} className="space-y-3 sm:space-y-4">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Nom *</label>
                    <input
                      type="text"
                      required
                      value={newProduct.name}
                      onChange={(e) => setNewProduct(prev => ({...prev, name: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                      placeholder="Nom du produit"
                    />
                  </div>
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Prix (f) *</label>
                    <input
                      type="number"
                      step="100"
                      required
                      value={newProduct.price}
                      onChange={(e) => setNewProduct(prev => ({...prev, price: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                      placeholder="1500"
                    />
                  </div>
                </div>
                
                <div>
                  <label className="block text-xs sm:text-sm font-medium text-gray-700">Description</label>
                  <textarea
                    value={newProduct.description}
                    onChange={(e) => setNewProduct(prev => ({...prev, description: e.target.value}))}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    placeholder="Description"
                    rows="2"
                  />
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Catégorie *</label>
                    <select
                      required
                      value={newProduct.category}
                      onChange={(e) => setNewProduct(prev => ({...prev, category: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    >
                      <option value="">Catégorie</option>
                      {categories.map(cat => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Image URL</label>
                    <input
                      type="url"
                      value={newProduct.image}
                      onChange={(e) => setNewProduct(prev => ({...prev, image: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                      placeholder="URL image"
                    />
                  </div>
                </div>

                <div className="flex items-center">
                  <input
                    type="checkbox"
                    checked={newProduct.isAvailable}
                    onChange={(e) => setNewProduct(prev => ({...prev, isAvailable: e.target.checked}))}
                    className="h-4 w-4 text-orange-600 focus:ring-orange-500 border-gray-300 rounded"
                  />
                  <label className="ml-2 block text-xs sm:text-sm text-gray-900">
                    Produit disponible
                  </label>
                </div>

                <div className="flex flex-col sm:flex-row gap-2 sm:space-x-3">
                  <button
                    type="submit"
                    className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 text-sm"
                  >
                    Ajouter
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowAddProduct(false)}
                    className="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 text-sm"
                  >
                    Annuler
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Formulaire modification - identique mais avec editingProduct */}
          {editingProduct && (
            <div className="mb-6 p-3 sm:p-4 border-2 border-dashed border-blue-300 rounded-lg bg-blue-50">
              <h3 className="text-base sm:text-lg font-semibold mb-3 sm:mb-4">Modifier</h3>
              <form onSubmit={handleEditProduct} className="space-y-3 sm:space-y-4">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Nom *</label>
                    <input
                      type="text"
                      required
                      value={editingProduct.name}
                      onChange={(e) => setEditingProduct(prev => ({...prev, name: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    />
                  </div>
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Prix (f) *</label>
                    <input
                      type="number"
                      step="100"
                      required
                      value={editingProduct.price}
                      onChange={(e) => setEditingProduct(prev => ({...prev, price: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    />
                  </div>
                </div>
                
                <div>
                  <label className="block text-xs sm:text-sm font-medium text-gray-700">Description</label>
                  <textarea
                    value={editingProduct.description}
                    onChange={(e) => setEditingProduct(prev => ({...prev, description: e.target.value}))}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    rows="2"
                  />
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Catégorie *</label>
                    <select
                      required
                      value={editingProduct.category}
                      onChange={(e) => setEditingProduct(prev => ({...prev, category: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    >
                      {categories.map(cat => (
                        <option key={cat} value={cat}>{cat}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-xs sm:text-sm font-medium text-gray-700">Image URL</label>
                    <input
                      type="url"
                      value={editingProduct.image}
                      onChange={(e) => setEditingProduct(prev => ({...prev, image: e.target.value}))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
                    />
                  </div>
                </div>

                <div className="flex items-center">
                  <input
                    type="checkbox"
                    checked={editingProduct.isAvailable}
                    onChange={(e) => setEditingProduct(prev => ({...prev, isAvailable: e.target.checked}))}
                    className="h-4 w-4 text-orange-600 focus:ring-orange-500 border-gray-300 rounded"
                  />
                  <label className="ml-2 block text-xs sm:text-sm text-gray-900">
                    Produit disponible
                  </label>
                </div>

                <div className="flex flex-col sm:flex-row gap-2 sm:space-x-3">
                  <button
                    type="submit"
                    className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 text-sm"
                  >
                    Modifier
                  </button>
                  <button
                    type="button"
                    onClick={() => setEditingProduct(null)}
                    className="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 text-sm"
                  >
                    Annuler
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Liste des produits */}
          <div className="space-y-3 sm:space-y-4">
            {products.map(product => (
              <div key={product.id} className="border rounded-lg p-3 sm:p-4">
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3">
                  <div className="flex items-start space-x-3 flex-1">
                    <img 
                      src={product.image} 
                      alt={product.name}
                      className="w-16 h-16 sm:w-20 sm:h-20 object-cover rounded-lg flex-shrink-0"
                    />
                    <div className="flex-1 min-w-0">
                      <h3 className="font-semibold text-base sm:text-lg truncate">{product.name}</h3>
                      <p className="text-gray-600 text-xs sm:text-sm line-clamp-2">{product.description}</p>
                      <div className="flex flex-wrap items-center gap-2 mt-1">
                        <span className="text-orange-600 font-bold text-sm sm:text-base">{product.price}f</span>
                        <span className="text-gray-500 text-xs sm:text-sm">• {product.category}</span>
                        <span className={`text-xs sm:text-sm ${product.isAvailable ? 'text-green-600' : 'text-red-600'}`}>
                          {product.isAvailable ? 'Disponible' : 'Indisponible'}
                        </span>
                      </div>
                    </div>
                  </div>
                  
                  <div className="flex items-center justify-end space-x-2">
                    <button
                      onClick={() => setEditingProduct(product)}
                      className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
                      title="Modifier"
                    >
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                      </svg>
                    </button>
                    
                    <button
                      onClick={() => toggleAvailability(product.id)}
                      className={`p-2 rounded ${
                        product.isAvailable 
                          ? 'bg-yellow-500 text-white hover:bg-yellow-600' 
                          : 'bg-green-500 text-white hover:bg-green-600'
                      }`}
                      title={product.isAvailable ? 'Rendre indisponible' : 'Rendre disponible'}
                    >
                      {product.isAvailable ? '🟡' : '🟢'}
                    </button>
                    
                    <button
                      onClick={() => handleDeleteProduct(product.id)}
                      className="bg-red-500 text-white p-2 rounded hover:bg-red-600"
                      title="Supprimer"
                    >
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                      </svg>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {products.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <p className="text-sm sm:text-base">Aucun produit pour le moment.</p>
              <button
                onClick={() => setShowAddProduct(true)}
                className="mt-2 text-orange-600 hover:text-orange-700 text-sm sm:text-base"
              >
                Ajoutez votre premier produit !
              </button>
            </div>
          )}
        </div>

        {/* Section Commandes - visible sur desktop ou si onglet actif sur mobile */}
        <div className={`bg-white rounded-lg shadow-md p-4 sm:p-6 ${
          activeTab === 'orders' ? 'block' : 'hidden lg:block'
        }`}>
          <h2 className="text-lg sm:text-xl font-bold text-gray-900 mb-4">Commandes en cours</h2>
          <div className="space-y-3 sm:space-y-4">
            {orders.map(order => (
              <div key={order.id} className="border rounded-lg p-3 sm:p-4">
                <div className="flex justify-between items-start mb-2">
                  <div>
                    <h3 className="font-semibold text-sm sm:text-base">#{order.id}</h3>
                    <p className="text-gray-600 text-xs sm:text-sm">{order.customer}</p>
                    <p className="text-xs sm:text-sm text-gray-500">{order.items}</p>
                  </div>
                  <div className="text-right">
                    <p className="font-bold text-orange-600 text-sm sm:text-base">{order.total}f</p>
                    <p className="text-xs sm:text-sm text-gray-500">{order.time}</p>
                  </div>
                </div>
                
                <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-2 mt-3">
                  <span className={`px-2 py-1 text-xs rounded-full w-fit ${getStatusColor(order.status)}`}>
                    {order.status === 'pending' && 'En attente'}
                    {order.status === 'preparing' && 'En préparation'}
                    {order.status === 'ready' && 'Prête'}
                    {order.status === 'delivered' && 'Livrée'}
                  </span>
                  
                  <div className="flex gap-2">
                    {order.status === 'pending' && (
                      <button
                        onClick={() => updateOrderStatus(order.id, 'preparing')}
                        className="bg-blue-500 text-white px-3 py-1 rounded text-xs sm:text-sm hover:bg-blue-600 flex-1 sm:flex-none"
                      >
                        Accepter
                      </button>
                    )}
                    {order.status === 'preparing' && (
                      <button
                        onClick={() => updateOrderStatus(order.id, 'ready')}
                        className="bg-green-500 text-white px-3 py-1 rounded text-xs sm:text-sm hover:bg-green-600 flex-1 sm:flex-none"
                      >
                        Prête
                      </button>
                    )}
                    {order.status === 'ready' && (
                      <button
                        onClick={() => updateOrderStatus(order.id, 'delivered')}
                        className="bg-gray-500 text-white px-3 py-1 rounded text-xs sm:text-sm hover:bg-gray-600 flex-1 sm:flex-none"
                      >
                        Livrée
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {orders.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <p className="text-sm sm:text-base">Aucune commande pour le moment.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  </div>
)
}

export default Dashboard